mapper.insert:
insert into transaction_local (hash, type, tx_index,
block_height, create_time, fee,transferType,
remark, txData, sign
)
values (#{hash,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, #{txIndex,jdbcType=INTEGER},
#{blockHeight,jdbcType=BIGINT}, #{createTime,jdbcType=BIGINT}, #{fee,jdbcType=BIGINT},
#{transferType,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, #{txData,jdbcType=VARBINARY}, #{sign,jdbcType=VARBINARY}
)

mapper.select:
select b.*
from tx_account_relation a
join `transaction_local` b on a.tx_hash = b.`hash`


    and a.address = #{address,jdbcType=VARCHAR}


    and b.type = #{type,jdbcType=INTEGER}


order by block_height asc, create_time asc

     limit #{start,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}
